def knuth_morris_pratt(text, pattern):
    lengths = get_lengths(pattern)
    matches = []
    m, i = 0, 0
    while m + i < len(text):
        if pattern[i] == text[m + i]:
            i += 1
            if i == len(pattern):
                matches.append(m)
                m += i - lengths[i - 1]
                i = max(lengths[i - 1], 0)
        else:
            m += i - lengths[i - 1]
            i = max(lengths[i - 1], 0)
    return matches

def get_lengths(pattern):
    lengths = [0] * len(pattern)
    length = 0
    for i in range(1, len(pattern)):
        while length > 0 and pattern[length] != pattern[i]:
            length = lengths[length - 1]
        if pattern[length] == pattern[i]:
            length += 1
        lengths[i] = length
    return lengths

